import java.util.*;

public class minWindow {
    public static String minWindow(String s, String t) {
        char[] s_array = s.toCharArray();
        char[] t_array = t.toCharArray();
        int[] map = new int[256];
        int end = 0;

        int start = 0;
        int min_length = Integer.MAX_VALUE;
        for (int i = 0; i < t_array.length; i++) // store t character and its count ----< means the lack number
            map[t_array[i]]++;
        int count = t_array.length;  //number of character that we have to match in the current window of string s
        int min_start = 0;

        // for those Characters t doesn't have, map[thisCharacter] are at most 0
        while (end < s_array.length) {
            if (map[s_array[end]] > 0) //if t has the current detected character as count of all other character except those in t =0
            // This is creating a sliding window, we are increasing end pointer(at bottom of
                                       // while loop) till all the characters in t string are not found in the current
                                       // window. If all char in t are in window, count will be 0
            {
                count--;  
            }
            map[s_array[end]]--; // reduce count of that char from map in window if one of those char in t found
                                 // in s

            while (count == 0) // window matches --> all char in t are in window , also the count of all chars in t is set to 0
            {
                if ((end - start + 1) < min_length) { 
                    min_length = end - start + 1;
                    min_start = start;
                }

                map[s_array[start]]++;  //increase the count of char in t in the map

                if (map[s_array[start]] > 0) { // remove a character which t has
                    count++; // Cause for those Characters t doesn't have, map[thisCharacter] are at most 0
                }
                start++; // start go right --> we shrink the window to that amount such that count is
                         // still =0,i.e we still have all the char in t in the minwindow substring of s  
            }
            end++;

        }
        System.out.println(min_length);
        if (min_start + min_length > s_array.length)
            return "";
        return s.substring(min_start, min_start + min_length);
    }

    public static void main(String[] args) {
        String s = "";
        String t = "";
        System.out.println(minWindow(s, t));
    }
}
