//264/266 Test Cases Passed :(, TLE at 264th testcase in leetcode
//Better Sol using hashmap and storign count of each char in t
// java minWindowOptimized.java
import java.util.ArrayList;

public class minWindow_mySol {
    public static void main(String[] args) {
        String s = "";
        String t = "";

        char[] charArray = t.toCharArray();
        ArrayList<Character> charList = new ArrayList<Character>();
        ArrayList<Character> t_temp = new ArrayList<Character>();
        for (char ch : charArray) {
            charList.add(ch);
            t_temp.add(ch);
        }
        int sIdx = 0;
        int eIdx = 0;
        int currLen = 0, minLen = s.length(), s_ans = -1, e_ans = -1;
        if (s == "" || t == "" || t.length() > s.length()) {
            System.out.println("");
        } else {
            while (sIdx <= s.length() - t.length() && sIdx <= eIdx) {

                int window_start = sIdx; // starting pointer in the current window

                while (eIdx < s.length()) { // expand current window to right till all the characters in t are not found
                                            // in the current window
                                            // this is done by storing all the character of t in a character arraylist
                    if (t_temp.contains(s.charAt(window_start))) {
                        t_temp.remove(Character.valueOf(s.charAt(window_start))); // if charcater in window matches a
                                                                                  // character in string t
                                                                                  // remove the character form arraylist
                    }
                    if (window_start != eIdx) { // this is for cases starting pointer of window is before the end index
                                                // of window
                                                // we only expand when the starting pointer == end index of window
                        window_start++;
                        continue;
                    }

                    if (t_temp.isEmpty()) { // if all the chars in string t are found in the current window, calculate
                                            // length and break loop
                        currLen = eIdx - sIdx + 1;
                        break;
                    }

                    window_start++; // if the window starting pointer == end index, increase them together;
                    eIdx++;
                }

                if (eIdx > s.length() - 1) { // this fixes the while loop last run problem
                                             // while loop runs one extra time
                    eIdx--;
                    currLen--;
                }

                if (minLen >= currLen && t_temp.isEmpty()) { // to find the minimum of the window which contains all
                                                             // chars of t
                    minLen = currLen;
                    s_ans = sIdx;
                    e_ans = eIdx;
                }

                if (sIdx == eIdx) {
                    break;
                }

                t_temp.clear(); // we clear our arraylist in case some characters were not found in the window
                for (char ch : charArray) { // fill it again
                    t_temp.add(ch);
                }

                while (sIdx <= eIdx) { // shrinking window from start index until some character form t is = s[sIdx]
                    if (sIdx == s.length() - 1) {
                        break;
                    }
                    sIdx++;
                    if (t_temp.contains(s.charAt(sIdx))) {
                        break;
                    }
                }
            }
            if (s_ans == -1 && e_ans == -1) {
                System.out.println("");
            } else {
                System.out.println(minLen + " " + s.substring(s_ans, e_ans + 1));
            }
        }
    }
}
